export default {
  props: ['title'],
  data() {
    return {
      artList: [],
      queryParam: {
        pagesize: 5,
        pagenum: 1
      },
      total: 0,
      isLoad: false
    };
  },
  mounted() {
    this.getArtList();
  },
  methods: {
    // 获取文章列表
    async getArtList() {
      const {
        data: res
      } = await this.$http.get('article', {
        params: {
          title: this.title,
          pagesize: this.queryParam.pagesize,
          pagenum: this.queryParam.pagenum
        }
      });
      this.artList = res.data;
      this.total = res.total;
      this.isLoad = true;
    }
  }
};
