export default {
  data() {
    return {
      profileInfo: {
        id: 1
      }
    };
  },
  created() {
    this.getProfileInfo();
  },
  methods: {
    // 获取个人设置
    async getProfileInfo() {
      const {
        data: res
      } = await this.$http.get(`profile/${this.profileInfo.id}`);
      this.profileInfo = res.data;
      this.$root.$emit('msg', res.data.icp_record);
    }
  }
};
